// view-topics.component.ts
import { Component } from '@angular/core';
import { TopicServiceService, Topic } from '../services copy/topic-service.service';

@Component({
  selector: 'app-view-topics',
  templateUrl: './view-topics.component.html',
  styleUrls: ['./view-topics.component.css']
})
export class ViewTopicsComponent {

  topics: Topic[] = [];
  loading: boolean = false;
  errorMessage: string = '';

  constructor(private topicService: TopicServiceService) { }

  ngOnInit(): void {
    this.loadTopics();
  }

  loadTopics() {
    this.loading = true;
    this.topicService.getAllTopics().subscribe({
      next: (data) => {
        this.topics = data;
        this.loading = false;
      },
      error: (err) => {
        console.error('Error fetching topics:', err);
        this.errorMessage = err?.error || 'Error fetching topics';
        this.loading = false;
      }
    });
  }
}
<div class="view-topic-container">
  <h2>All Topics</h2>

  <div *ngIf="loading" class="loading">Loading...</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>

  <table *ngIf="topics.length > 0">
    <thead>
      <tr>
        <th>ID</th>
        <th>Code</th>
        <th>Name</th>
        <th>Category</th>
        <th>Description</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let topic of topics">
        <td>{{ topic.topicId }}</td>
        <td>{{ topic.topicCode }}</td>
        <td>{{ topic.topicName }}</td>
        <td>{{ topic.category }}</td>
        <td>{{ topic.description }}</td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!loading && topics.length === 0">No topics found.</div>
</div>
