import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, SignInRequest } from '../auth.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {
  request: SignInRequest = { username: '', password: '' };
  errorMessage = '';
  loading = false;

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit() {
    this.errorMessage = '';

    // ✅ Validate input length
    if (this.request.username.length < 6 || this.request.password.length < 6) {
      this.errorMessage = 'Username and password must be at least 6 characters.';
      return;
    }

    this.loading = true;

    this.authService.signin(this.request).subscribe({
      next: (res) => {
        this.authService.saveToken(res.token);
        this.loading = false;
        this.router.navigate(['/dashboard']); // redirect
      },
      error: (err) => {
        this.loading = false;
        this.errorMessage = err.error?.message || 'Login failed';
        console.error(err);
      }
    });
  }
}
<div class="login-container">
  <h2>Sign In</h2>
  <form (ngSubmit)="onSubmit()">
    <div>
      <label>Username</label>
      <input type="text" [(ngModel)]="request.username" name="username" required>
    </div>

    <div>
      <label>Password</label>
      <input type="password" [(ngModel)]="request.password" name="password" required>
    </div>

    <button type="submit">Login</button>
    <p class="error" *ngIf="errorMessage">{{ errorMessage }}</p>
  </form>
</div>
