import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-event-enroll',
  templateUrl: './participant-eventenroll.component.html',
  styleUrls: ['./event-enroll.component.css']
})
export class EventEnrollComponent {
  participantId: number | null = null;
  eventId: number | null = null;
  loading = false;
  message = '';

  constructor(private http: HttpClient) {}

  enroll() {
    if (!this.participantId || !this.eventId) {
      this.message = 'Please select both participant and event.';
      return;
    }

    this.loading = true;

    this.http
      .post(
        `http://localhost:5070/api/Participants/${this.participantId}/Enroll/${this.eventId}`,
        {}
      )
      .subscribe({
        next: (res: any) => {
          this.message = res.message || 'Enrollment successful!';
          this.loading = false;
        },
        error: (err) => {
          console.error('Error enrolling:', err);
          this.message = 'Enrollment failed. Please try again.';
          this.loading = false;
        }
      });
  }
}
<div class="page-container">
  <h2>Enroll Participant in Event</h2>

  <form (ngSubmit)="enroll()" #enrollForm="ngForm">
    <!-- Participant Selection -->
    <div class="form-group">
      <label for="participant">Select Participant:</label>
      <select
        id="participant"
        name="participantId"
        [(ngModel)]="participantId"
        required
      >
        <option [ngValue]="null">-- Select Participant --</option>
        <option [ngValue]="1">1 - Reddy</option>
        <option [ngValue]="2">2 - Srinivas</option>
        <option [ngValue]="3">3 - Vishwa</option>
      </select>
    </div>

    <!-- Event Selection -->
    <div class="form-group">
      <label for="event">Select Event:</label>
      <select
        id="event"
        name="eventId"
        [(ngModel)]="eventId"
        required
      >
        <option [ngValue]="null">-- Select Event --</option>
        <option [ngValue]="101">101 - Angular Workshop</option>
        <option [ngValue]="102">102 - .NET Bootcamp</option>
        <option [ngValue]="103">103 - Cloud Conference</option>
      </select>
    </div>

    <!-- Enroll Button -->
    <button type="submit" [disabled]="loading || enrollForm.invalid">
      {{ loading ? 'Enrolling...' : 'Enroll' }}
    </button>
  </form>

  <!-- Message -->
  <div *ngIf="message" class="message">
    {{ message }}
  </div>
</div>
