import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

// Participant and Feedback models
export interface Participant {
  participantId?: number;
  name: string;
  email: string;
  phone: string;
  user?: {
    userId?: number;
    username: string;
    password: string;
  };
}

export interface Feedback {
  feedbackId?: number;
  eventID: number;
  speakerID: number;
  feedback_remarks: string;
}

@Injectable({
  providedIn: 'root'
})
export class ParticipantsService {
  private baseUrl = 'http://localhost:5070/api/Participants'; // Base URL for the API

  constructor(private http: HttpClient) {}

  // Register a new participant
  register(participant: Participant): Observable<any> {
    return this.http.post(`${this.baseUrl}/Register`, participant);
  }

  // Enroll a participant in an event
  enroll(participantId: number, eventId: number): Observable<any> {
    return this.http.post(`${this.baseUrl}/${participantId}/Enroll/${eventId}`, {});
  }

  // Update participant details (name, email, phone, etc.)
  update(id: number, participant: Participant): Observable<any> {
    return this.http.put(`${this.baseUrl}/${id}`, participant);
  }

  // Submit feedback for an event
  submitFeedback(feedback: Feedback): Observable<any> {
    return this.http.post(`${this.baseUrl}/Feedback`, feedback);
  }

  // Fetch all events (if you have an endpoint for this)
  getEvents(): Observable<any> {
    return this.http.get('http://localhost:5070/api/Events'); // Assuming the API URL for events
  }

  // Fetch all speakers (if you have an endpoint for this)
  getSpeakers(): Observable<any> {
    return this.http.get('http://localhost:5070/api/Speakers'); // Assuming the API URL for speakers
  }
}
