import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

// Define the Administrator interface
export interface Administrator {
  adminId?: number;
  name: string;
  email: string;
  phone: string;
  userId?: number;
}

@Injectable({
  providedIn: 'root'
})
export class AdminService {
  private baseUrl = 'http://localhost:5070/api/Administrator';  // Base URL for the Admin API

  constructor(private http: HttpClient) { }

  // Add an administrator with username and password as query parameters
  addAdmin(admin: Administrator, username: string, password: string): Observable<any> {
    const params = new HttpParams()
      .set('username', username)
      .set('password', password);

    return this.http.post<any>(
      `${this.baseUrl}/Admin/Add`,
      admin,       // Admin data in the body
      { params }   // Query parameters
    );
  }

  // Get all administrators
  getAllAdmins(): Observable<Administrator[]> {
    return this.http.get<Administrator[]>(`${this.baseUrl}/Admin/View`);
  }

  // Add a promotional code
  addPromoCode(promoCode: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}/PromoCode/add`, promoCode);
  }

  // Get all promotional codes
  getAllPromoCodes(): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/PromoCode/view`);
  }

  // Delete a promotional code by its ID
  deletePromoCode(id: number): Observable<any> {
    return this.http.delete<any>(`${this.baseUrl}/PromoCode/delete/${id}`);
  }

  // Add a location
  addLocation(location: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}/Location/Add`, location);
  }

  // Get all locations
  getAllLocations(): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/Location/View`);
  }

  // Get all events
  getAllEvents(): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/Event/View`);
  }

  // Add an event
  addEvent(event: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}/Event/Add`, event);
  }

  // Add a speaker
  addSpeaker(speaker: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}/Speakers/Add`, speaker);
  }

  // Get all speakers
  getAllSpeakers(): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/Speakers/View`);
  }
}
