import { Component, OnInit } from '@angular/core';
import { PromocodeService, PromoCode } from '../services/promocode.service';

@Component({
  selector: 'app-view-promocode',
  templateUrl: './view-promocode.component.html',
  styleUrls: ['./view-promocode.component.css']
})
export class ViewPromocodeComponent implements OnInit {

  promoList: PromoCode[] = [];

  constructor(private promoService: PromocodeService) { }

  ngOnInit(): void {
    this.loadPromoCodes();
  }

  loadPromoCodes() {
    this.promoService.getPromoCodes().subscribe({
      next: (data) => this.promoList = data,
      error: (err) => {
        console.error('Error fetching promo codes:', err);
        alert('Failed to load promo codes from database.');
      }
    });
  }
}
<div class="promocode-view">
  <h2>Promo Codes</h2>

  <!-- Add Button -->
  <div class="top-actions">
    <button routerLink="/create-promocode">Add New Promo Code</button>
  </div>

  <!-- Promo Codes Table -->
  <table>
    <thead>
      <tr>
        <th>Code</th>
        <th>Valid From</th>
        <th>Valid To</th>
        <th>Discount (%)</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let promo of promoList">
        <td>{{ promo.code }}</td>
        <td>{{ promo.validFrom }}</td>
        <td>{{ promo.validTo }}</td>
        <td>{{ promo.discountPercentage }}</td>
      </tr>
    </tbody>
  </table>
</div>
