import { Component } from '@angular/core';
import { AdminService } from '../services/admin.service';  // Adjust path if needed
import { Router } from '@angular/router';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {

  activeMenu: string = '';  // This variable stores the currently active menu

  constructor(private adminService: AdminService, private router: Router) {}

  toggleMenu(menu: string): void {
    // Toggle the active menu when clicking on it
    this.activeMenu = this.activeMenu === menu ? '' : menu;
  }
}
