import { Component, OnInit } from '@angular/core';
import { SpeakerService, Event } from '../speaker.service';

@Component({
  selector: 'app-enroll-event',
  templateUrl: './enroll-event.component.html',
  styleUrls: ['./enroll-event.component.css']
})
export class EnrollEventComponent implements OnInit {
  speakerId = ''; // 🔹 replace with logged-in speaker’s ID (later from JWT)
  events: Event[] = [];
  successMessage = '';
  errorMessage = '';
  loading = false;

  constructor(private speakerService: SpeakerService) {}

  ngOnInit(): void {
    this.loadEvents();
  }

  loadEvents() {
    this.loading = true;
    this.speakerService.getAvailableEvents(this.speakerId).subscribe({
      next: (res) => {
        this.events = res;
        this.loading = false;
      },
      error: (err) => {
        this.errorMessage = '❌ Failed to load events';
        this.loading = false;
      }
    });
  }

  enroll(eventId: number) {
    this.successMessage = '';
    this.errorMessage = '';
    this.speakerService.enrollInEvent(this.speakerId, eventId).subscribe({
      next: () => {
        this.successMessage = `✅ Successfully enrolled in event #${eventId}`;
        this.loadEvents(); // reload events after enrolling
      },
      error: () => {
        this.errorMessage = '❌ Failed to enroll in event';
      }
    });
  }
}
<div class="enroll-container">
  <h2>Enroll in Events</h2>

  <div *ngIf="loading">Loading events...</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>

  <table *ngIf="events.length > 0" class="events-table">
    <thead>
      <tr>
        <th>Event ID</th>
        <th>Course Title</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let e of events">
        <td>{{ e.eventId }}</td>
        <td>{{ e.courseTitle }}</td>
        <td>{{ e.startDate | date }}</td>
        <td>{{ e.endDate | date }}</td>
        <td>
          <button (click)="enroll(e.eventId)" class="enroll-btn">Enroll</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!loading && events.length === 0">
    🎉 No events available to enroll.
  </div>
</div>
