import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface SignInRequest {
  username: string;
  password: string;
}

export interface SignUpRequest {
  username: string;
  password: string;
  role: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'http://localhost:5070/api/Auth'; // Base API URL

  constructor(private http: HttpClient) {}

  // üîë Sign in
  signin(request: SignInRequest): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/signin`, request);
  }

  // üìù Sign up
  signup(request: SignUpRequest): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/signup`, request);
  }

  // üíæ Save token
  saveToken(token: string) {
    localStorage.setItem('token', token);
  }

  // üìÇ Get token
  getToken(): string | null {
    return localStorage.getItem('token');
  }

  // üö™ Logout
  logout() {
    localStorage.removeItem('token');
  }

  // ‚úÖ Check if logged in
  isLoggedIn(): boolean {
    return !!this.getToken();
  }
}
