import { Component } from '@angular/core';
import { ParticipantsService, Participant } from '../participant.service';

@Component({
  selector: 'app-participant-update',
  templateUrl: './participant-update.component.html',
  styleUrls: ['./participant-update.component.css']
})
export class ParticipantUpdateComponent {
  id!: number;
  participant: Participant = { name: '', email: '', phone: '' };
  message = '';

  constructor(private service: ParticipantsService) {}

  update() {
    this.service.update(this.id, this.participant).subscribe({
      next: res => this.message = res.message,
      error: err => this.message = err.error
    });
  }
}
<h2>Update Participant</h2>
<form (ngSubmit)="update()">
  <input [(ngModel)]="id" name="id" placeholder="Participant ID" required />
  <input [(ngModel)]="participant.name" name="name" placeholder="Name" />
  <input [(ngModel)]="participant.email" name="email" placeholder="Email" />
  <input [(ngModel)]="participant.phone" name="phone" placeholder="Phone" />
  <button type="submit">Update</button>
</form>

<p>{{ message }}</p>
