import { Component } from '@angular/core';
import { SpeakerService, SpeakerRegistration } from '../speaker.service';

@Component({
  selector: 'app-register-speaker',
  templateUrl: './register-speaker.component.html',
  styleUrls: ['./register-speaker.component.css']
})
export class RegisterSpeakerComponent {
  speaker: SpeakerRegistration = {
    username: '',
    password: '',
    name: '',
    email: '',
    phone: '',
    address: '',
    keySkills: []
  };

  successMessage = '';
  errorMessage = '';
  loading = false;

  constructor(private speakerService: SpeakerService) {}

  onSubmit(form: any) {
    if (form.invalid) return;

    this.successMessage = '';
    this.errorMessage = '';
    this.loading = true;

    if (typeof this.speaker.keySkills === 'string') {
      this.speaker.keySkills = (this.speaker.keySkills as unknown as string)
        .split(',')
        .map(skill => skill.trim());
    }

    this.speakerService.registerSpeaker(this.speaker).subscribe({
      next: () => {
        this.loading = false;
        this.successMessage = '✅ Speaker registered successfully!';
        this.speaker = {
          username: '',
          password: '',
          name: '',
          email: '',
          phone: '',
          address: '',
          keySkills: []
        };
        form.resetForm();
      },
      error: (err) => {
        this.loading = false;
        this.errorMessage = err.error?.message || '❌ Failed to register speaker';
      }
    });
  }
}
<div class="register-container">
  <form class="register-form" (ngSubmit)="onSubmit()">
    <h2>Register Speaker</h2>

    <div class="form-group">
      <label>Username</label>
      <input type="text" [(ngModel)]="speaker.username" name="username" required />
    </div>

    <div class="form-group">
      <label>Password</label>
      <input type="password" [(ngModel)]="speaker.password" name="password" required />
    </div>

    <div class="form-group">
      <label>Name</label>
      <input type="text" [(ngModel)]="speaker.name" name="name" required />
    </div>

    <div class="form-group">
      <label>Email</label>
      <input type="email" [(ngModel)]="speaker.email" name="email" required />
    </div>

    <div class="form-group">
      <label>Phone</label>
      <input type="text" [(ngModel)]="speaker.phone" name="phone" required />
    </div>

    <div class="form-group">
      <label>Address</label>
      <input type="text" [(ngModel)]="speaker.address" name="address" />
    </div>

    <div class="form-group">
      <label>Key Skills (comma separated)</label>
      <input type="text" [(ngModel)]="speaker.keySkills" name="keySkills" />
    </div>

    <button type="submit" class="register-btn" [disabled]="loading">
      {{ loading ? 'Registering...' : 'Register Speaker' }}
    </button>
  </form>

  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
</div>
