import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface SpeakerRegistration {
  username: string;
  password: string;
  name: string;
  email: string;
  phone: string;
  address: string;
  keySkills: string[];
}

export interface Event {
  eventId: number;
  courseTitle: string;
  topicId: number;
  locationId: number;
  speakerId?: number;
  classSize: number;
  numberOfDays: number;
  startDate: string;
  endDate: string;
}

export interface Feedback {
  feedbackId: number;
  eventId: number;
  speakerId: number;
  comments: string;
  rating: number;
}

@Injectable({
  providedIn: 'root'
})
export class SpeakerService {
  private apiUrl = 'http://localhost:5070/api/Speaker';

  constructor(private http: HttpClient) {}

  // Register a new speaker
  registerSpeaker(data: SpeakerRegistration): Observable<any> {
    return this.http.post(`${this.apiUrl}/register`, data);
  }

  // Get available events
  getEvents(speakerId: number): Observable<Event[]> {
    return this.http.get<Event[]>(`${this.apiUrl}/${speakerId}/events`);
  }

  // Enroll in an event
  enrollInEvent(speakerId: number, eventId: number): Observable<any> {
    return this.http.post(`${this.apiUrl}/${speakerId}/enroll/${eventId}`, {});
  }

  // Get feedback for a speaker
  getFeedback(speakerId: number): Observable<Feedback[]> {
    return this.http.get<Feedback[]>(`${this.apiUrl}/${speakerId}/feedback`);
  }
}
