import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Participant {
  participantId: number;
  name: string;
  email: string;
  phone: string;
}

@Component({
  selector: 'app-participant-update',
  templateUrl: './participant-update.component.html',
  styleUrls: ['./participant-update.component.css']
})
export class ParticipantUpdateComponent {
  participant: Participant = {
    participantId: 0,
    name: '',
    email: '',
    phone: ''
  };

  loading = false;
  successMessage = '';
  errorMessage = '';

  private apiUrl = 'http://localhost:5070/api/Participants';

  constructor(private http: HttpClient) {}

  onSubmit(form: any) {
    if (form.invalid) {
      this.errorMessage = 'Please fill in all required fields correctly.';
      return;
    }

    this.loading = true;
    this.successMessage = '';
    this.errorMessage = '';

    this.http.put<any>(`${this.apiUrl}/${this.participant.participantId}`, this.participant)
      .subscribe({
        next: (res) => {
          this.successMessage = res.message || 'Participant updated successfully!';
          this.loading = false;
        },
        error: (err) => {
          console.error('Error updating participant:', err);
          this.errorMessage = err.error || 'Failed to update participant. Please try again.';
          this.loading = false;
        }
      });
  }
}
<div class="page-container">
  <form #updateForm="ngForm" (ngSubmit)="onSubmit(updateForm)">
    <h2>Update Participant</h2>

    <div class="form-group">
      <label for="participantId">Participant ID *</label>
      <input id="participantId" name="participantId" type="number" [(ngModel)]="participant.participantId" required />
      <div *ngIf="updateForm.submitted && !participant.participantId" class="error">
        Participant ID is required
      </div>
    </div>

    <div class="form-group">
      <label for="name">Name *</label>
      <input id="name" name="name" type="text" [(ngModel)]="participant.name" required />
      <div *ngIf="updateForm.submitted && !participant.name" class="error">
        Name is required
      </div>
    </div>

    <div class="form-group">
      <label for="email">Email *</label>
      <input id="email" name="email" type="email" [(ngModel)]="participant.email" required />
      <div *ngIf="updateForm.submitted && !participant.email" class="error">
        Valid email is required
      </div>
    </div>

    <div class="form-group">
      <label for="phone">Phone *</label>
      <input id="phone" name="phone" type="text" pattern="^[0-9]{10}$" [(ngModel)]="participant.phone" required />
      <div *ngIf="updateForm.submitted && !participant.phone" class="error">
        Phone number is required (10 digits)
      </div>
    </div>

    <button type="submit" [disabled]="updateForm.invalid || loading">
      {{ loading ? 'Updating...' : 'Update Participant' }}
    </button>
  </form>

  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
</div>
