import { Component, OnInit } from '@angular/core';
import { AdminService,Administrator } from '../services copy/admin.service';
@Component({
  selector: 'app-view-admin',
  templateUrl: './view-admin.component.html',
  styleUrls: ['./view-admin.component.css']
})
export class ViewAdminComponent implements OnInit {

  admins: Administrator[] = [];
  loading: boolean = false;
  errorMessage: string = '';

  constructor(private adminService: AdminService) { }

  ngOnInit(): void {
    this.loadAdmins();
  }

  loadAdmins() {
    this.loading = true;
    this.adminService.getAllAdmins().subscribe({
      next: (data) => {
        this.admins = data;
        this.loading = false;
      },
      error: (err) => {
        console.error('Error fetching admins:', err);
        this.errorMessage = err?.error || 'Error fetching administrators.';
        this.loading = false;
      }
    });
  }
}
<div class="view-container">
  <h2>All Administrators</h2>

  <div *ngIf="loading" class="loading">Loading...</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>

  <table *ngIf="admins.length > 0">
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Email</th>
        <th>Phone</th>
        <th>UserId</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let admin of admins">
        <td>{{ admin.adminId }}</td>
        <td>{{ admin.name }}</td>
        <td>{{ admin.email }}</td>
        <td>{{ admin.phone }}</td>
        <td>{{ admin.userId }}</td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="!loading && admins.length === 0">No administrators found.</div>
</div>
