import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, SignInRequest } from '../auth.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {
  request: SignInRequest = { 
    username: '', 
    password: '', 
    role: 'admin' 
  };

  errorMessage = '';
  successMessage = '';
  loading = false;

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit() {
    this.errorMessage = '';
    this.successMessage = '';

    if (this.request.username.length < 6 || this.request.password.length < 6) {
      this.errorMessage = 'Username and password must be at least 6 characters.';
      return;
    }

    this.loading = true;

    this.authService.signin(this.request).subscribe({
      next: (res) => {
        this.authService.saveToken(res.token); // store JWT
        this.loading = false;
        this.successMessage = res.message || 'Login successful!'; // display backend message
        // Optionally redirect after a short delay
        setTimeout(() => this.router.navigate(['/dashboard']), 1500);
      },
      error: (err) => {
        this.loading = false;
        this.errorMessage = err.error?.title || err.error?.message || 'Login failed';
        console.error('Signin error:', err);
      }
    });
  }
}
<div class="signin-container">
  <h2>Sign In</h2>

  <form (ngSubmit)="onSubmit()">
    <div>
      <label>Username:</label>
      <input type="text" [(ngModel)]="request.username" name="username" required minlength="6">
    </div>

    <div>
      <label>Password:</label>
      <input type="password" [(ngModel)]="request.password" name="password" required minlength="6">
    </div>

    <button type="submit" [disabled]="loading">Sign In</button>
  </form>

  <div *ngIf="loading">Logging in...</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
</div>
