import { Component } from '@angular/core';
import { SpeakerService, SpeakerRegistration } from '../speaker.service';

@Component({
  selector: 'app-register-speaker',
  templateUrl: './register-speaker.component.html',
  styleUrls: ['./register-speaker.component.css']
})
export class RegisterSpeakerComponent {
  speaker: SpeakerRegistration = {
    username: '',
    password: '',
    name: '',
    email: '',
    phone: '',
    address: '',
    keySkills: []
  };

  successMessage = '';
  errorMessage = '';
  loading = false;

  constructor(private speakerService: SpeakerService) {}

  onSubmit() {
    this.successMessage = '';
    this.errorMessage = '';
    this.loading = true;

    // Convert keySkills string to array if entered as comma separated
    if (typeof this.speaker.keySkills === 'string') {
      this.speaker.keySkills = (this.speaker.keySkills as unknown as string)
        .split(',')
        .map(skill => skill.trim());
    }

    this.speakerService.registerSpeaker(this.speaker).subscribe({
      next: () => {
        this.loading = false;
        this.successMessage = '✅ Speaker registered successfully!';
        // reset form
        this.speaker = {
          username: '',
          password: '',
          name: '',
          email: '',
          phone: '',
          address: '',
          keySkills: []
        };
      },
      error: (err) => {
        this.loading = false;
        this.errorMessage = err.error?.message || '❌ Failed to register speaker';
      }
    });
  }
}
<div class="form-container">
  <h2>📝 Register Speaker</h2>
  <form>
    <div class="form-group">
      <label>Username</label>
      <input type="text" placeholder="Enter username" />
    </div>
    <div class="form-group">
      <label>Password</label>
      <input type="password" placeholder="Enter password" />
    </div>
    <div class="form-group">
      <label>Name</label>
      <input type="text" placeholder="Full Name" />
    </div>
    <div class="form-group">
      <label>Email</label>
      <input type="email" placeholder="Email Address" />
    </div>
    <div class="form-group">
      <label>Phone</label>
      <input type="text" placeholder="Phone Number" />
    </div>
    <div class="form-group">
      <label>Address</label>
      <input type="text" placeholder="Address" />
    </div>
    <div class="form-group">
      <label>Key Skills</label>
      <input type="text" placeholder="e.g. Cloud, AI, DevOps" />
    </div>
    <button type="submit" class="btn-gold">Register</button>
  </form>
</div>
