import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface EventModel {
  eventId: number;
  courseTitle: string;
  topicId: number;
  locationId: number;
  speakerId: number | null;
  classSize: number;
  numberOfDays: number;
  startDate: string;
  endDate: string;
}

@Injectable({
  providedIn: 'root'
})
export class SpeakerService {
  private apiUrl = 'http://localhost:5070/api/Speaker';

  constructor(private http: HttpClient) {}

  getAvailableEvents(speakerId: number): Observable<EventModel[]> {
    return this.http.get<EventModel[]>(`${this.apiUrl}/${speakerId}/events`);
  }

  enrollInEvent(speakerId: number, eventId: number): Observable<any> {
    return this.http.post(`${this.apiUrl}/${speakerId}/enroll/${eventId}`, {});
  }
}
