import { Component, OnInit } from '@angular/core';
import { EventService, Event } from '../services copy/event.service';

@Component({
  selector: 'app-add-event',
  templateUrl: './add-event.component.html',
  styleUrls: ['./add-event.component.css']
})
export class AddEventComponent implements OnInit {
  newEvent: Event = {
    courseTitle: '',
    topicId: 0,
    locationId: 0,
    speakerId: 0,
    classSize: 0,
    numberOfDays: 0,
    startDate: '',
    endDate: ''
  };

  topics: any[] = [];
  locations: any[] = [];
  speakers: any[] = [];

  constructor(private eventService: EventService) {}

  ngOnInit(): void {
    this.loadDropdowns();
  }

  loadDropdowns(): void {
    this.eventService.getTopics().subscribe(data => this.topics = data);
    this.eventService.getLocations().subscribe(data => this.locations = data);
    this.eventService.getSpeakers().subscribe(data => this.speakers = data);
  }

  addEvent(): void {
    this.eventService.addEvent(this.newEvent).subscribe({
      next: (res) => {
        alert('Event added successfully!');
        this.resetForm();
      },
      error: (err) => {
        console.error('Error adding event:', err);
        alert('Failed to add event');
      }
    });
  }

  resetForm(): void {
    this.newEvent = {
      courseTitle: '',
      topicId: 0,
      locationId: 0,
      speakerId: 0,
      classSize: 0,
      numberOfDays: 0,
      startDate: '',
      endDate: ''
    };
  }
}
<div class="form-container">
  <h2>Add New Event</h2>
  <form (ngSubmit)="addEvent()">

    <!-- Course Title -->
    <label for="courseTitle">Course Title</label>
    <input type="text" id="courseTitle" placeholder="Enter Course Title"
           [(ngModel)]="newEvent.courseTitle" name="courseTitle" required />

    <!-- Dropdown for Topic -->
    <label for="topicId">Select Topic</label>
    <select id="topicId" [(ngModel)]="newEvent.topicId" name="topicId" required>
      <option value="" disabled selected>Select a Topic</option>
      <option *ngFor="let topic of topics" [value]="topic.topicId">
        {{ topic.topicName }}
      </option>
    </select>

    <!-- Dropdown for Location -->
    <label for="locationId">Select Location</label>
    <select id="locationId" [(ngModel)]="newEvent.locationId" name="locationId" required>
      <option value="" disabled selected>Select a Location</option>
      <option *ngFor="let location of locations" [value]="location.locationId">
        {{ location.city }} - {{ location.state }}
      </option>
    </select>

    <!-- Dropdown for Speaker -->
    <label for="speakerId">Select Speaker</label>
    <select id="speakerId" [(ngModel)]="newEvent.speakerId" name="speakerId" required>
      <option value="" disabled selected>Select a Speaker</option>
      <option *ngFor="let speaker of speakers" [value]="speaker.speakerId">
        {{ speaker.name }}
      </option>
    </select>

    <!-- Class Size -->
    <label for="classSize">Class Size</label>
    <input type="number" id="classSize" placeholder="Enter Class Size"
           [(ngModel)]="newEvent.classSize" name="classSize" required />

    <!-- Number of Days -->
    <label for="numberOfDays">Number of Days</label>
    <input type="number" id="numberOfDays" placeholder="Enter Number of Days"
           [(ngModel)]="newEvent.numberOfDays" name="numberOfDays" required />

    <!-- Start Date -->
    <label for="startDate">Start Date</label>
    <input type="date" id="startDate" placeholder="Select Start Date"
           [(ngModel)]="newEvent.startDate" name="startDate" required />

    <!-- End Date -->
    <label for="endDate">End Date</label>
    <input type="date" id="endDate" placeholder="Select End Date"
           [(ngModel)]="newEvent.endDate" name="endDate" required />

    <!-- Submit Button -->
    <button type="submit">Add Event</button>
  </form>
</div>
