import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-feedback',
  templateUrl: '../participant-feedback/participant-feedback.component.html',
  styleUrls: ['./participant-feedback.component.css']
})
export class FeedbackComponent {
  feedback = {
    eventId: null,
    speakerId: null,
    remarks: ''
  };

  message: string = '';
  isSuccess: boolean = false;

  constructor(private http: HttpClient) {}

  submitFeedback() {
    this.http.post('api/Participants/Feedback', this.feedback).subscribe({
      next: (response: any) => {
        this.message = response.message;
        this.isSuccess = true;
      },
      error: (err) => {
        this.message = 'Error submitting feedback: ' + err.error;
        this.isSuccess = false;
      }
    });
  }
}
<div class="page-container">
  <h2>Submit Feedback</h2>
  <form (ngSubmit)="submitFeedback()">
    <div class="form-group">
      <label for="eventId">Event ID:</label>
      <input [(ngModel)]="feedback.eventId" name="eventId" id="eventId" type="number" placeholder="Enter Event ID" required />
    </div>

    <div class="form-group">
      <label for="speakerId">Speaker ID:</label>
      <input [(ngModel)]="feedback.speakerId" name="speakerId" id="speakerId" type="number" placeholder="Enter Speaker ID" required />
    </div>

    <div class="form-group">
      <label for="remarks">Feedback Remarks:</label>
      <textarea [(ngModel)]="feedback.remarks" name="remarks" id="remarks" rows="4" placeholder="Enter your feedback here..." required></textarea>
    </div>

    <button type="submit">Submit Feedback</button>
  </form>

  <p *ngIf="message" [ngClass]="{'success': isSuccess, 'error': !isSuccess}">{{ message }}</p>
</div>
