import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-participant-sidebar',
  templateUrl: './participant-sidebar.component.html',
  styleUrls: ['./participant-sidebar.component.css']
})
export class ParticipantSidebarComponent {
    constructor(private authService: AuthService, private router: Router) {}

   logout() {
    this.authService.logout();         // remove token
    this.router.navigate(['/signin']); // redirect to signin
  }

}
<div class="sidebar">
  <h3>Participant Menu</h3>
  <ul>
    <li><a routerLink="/participant/register" routerLinkActive="active">Register</a></li>
    <li><a routerLink="/participant/enroll" routerLinkActive="active">Enroll in Event</a></li>
    <li><a routerLink="/participant/update" routerLinkActive="active">Update Profile</a></li>
    <li><a routerLink="/participant/feedback" routerLinkActive="active">Feedback</a></li>
    <li><a routerLink="/signin"> Logout</a></li>

  </ul>
</div>
