import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, SignInRequest } from '../auth.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {
  // Default role can be 'admin'
  request: SignInRequest = { 
    username: '', 
    password: '', 
    role: 'admin' 
  };

  errorMessage = '';
  successMessage = '';
  loading = false;

  // Roles available
  roles = ['admin', 'participant', 'speaker'];

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit() {
    this.errorMessage = '';
    this.successMessage = '';

    if (this.request.username.length < 6 || this.request.password.length < 6) {
      this.errorMessage = 'Username and password must be at least 6 characters.';
      return;
    }

    this.loading = true;

    this.authService.signin(this.request).subscribe({
      next: (res) => {
        this.authService.saveToken(res.token);
        this.loading = false;
        this.successMessage = res.message || 'Login successful!';
        setTimeout(() => this.router.navigate(['/dashboard']), 1500);
      },
      error: (err) => {
        this.loading = false;
        this.errorMessage = err.error?.title || err.error?.message || 'Login failed';
        console.error('Signin error:', err);
      }
    });
  }
}
<div class="signin-container">
  <form class="signin-form" (ngSubmit)="onSubmit()">
    <h2>Sign In</h2>

    <div class="form-group">
      <label for="username">Username</label>
      <input 
        type="text" 
        id="username" 
        name="username" 
        [(ngModel)]="request.username" 
        required 
        minlength="6"
        autocomplete="username"
      />
    </div>

    <div class="form-group">
      <label for="password">Password</label>
      <input 
        type="password" 
        id="password" 
        name="password" 
        [(ngModel)]="request.password" 
        required 
        minlength="6"
        autocomplete="current-password"
      />
    </div>

    <div class="form-group">
      <label for="role">Role</label>
      <select 
        id="role" 
        name="role" 
        [(ngModel)]="request.role" 
        required
      >
        <option *ngFor="let r of roles" [value]="r">{{ r | titlecase }}</option>
      </select>
    </div>

    <div class="actions">
      <button type="submit" class="signin-btn" [disabled]="loading">Sign In</button>
      <button type="button" class="forgot-btn">Forgot Password?</button>
    </div>
  </form>

  <div *ngIf="loading" class="loading">Logging in...</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
</div>
