import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-participant-register',
  templateUrl: './participant-register.component.html',
  styleUrls: ['./participant-register.component.css']
})
export class ParticipantRegisterComponent {
  // Defining the participant object structure
  participant: any = {
    name: '',
    email: '',
    phone: '',
    user: {
      username: '',
      password: ''
    }
  };

  loading = false;  // Flag for loading spinner
  successMessage = '';  // Holds the success message
  errorMessage = '';  // Holds the error message

  constructor(private http: HttpClient) {}

  // Function to handle form submission
  onSubmit(form: NgForm) {
    if (form.invalid) return;  // If the form is invalid, exit

    this.loading = true;  // Show loading spinner
    this.successMessage = '';  // Clear success message
    this.errorMessage = '';  // Clear error message

    // Prepare payload based on the form data
    const payload = {
      participantId: 0,  // Default participantId, as it is set by the backend
      name: this.participant.name,
      email: this.participant.email,
      phone: this.participant.phone,
      user: {
        username: this.participant.user.username,
        password: this.participant.user.password
      }
    };

    console.log('Sending payload:', payload);

    // POST request to the backend API for registration
    this.http.post('http://localhost:5070/api/Participants/Register', payload)
      .subscribe({
        next: (response) => {
          this.loading = false;  // Hide loading spinner
          this.successMessage = 'Participant registered successfully!';  // Show success message
          form.resetForm();  // Reset the form after successful registration
        },
        error: (err) => {
          this.loading = false;  // Hide loading spinner
          console.error('Error registering participant:', err);  // Log error
          this.errorMessage = 'Failed to register participant. Please check details.';  // Show error message
        }
      });
  }
}
<div class="register-container">
  <form #participantForm="ngForm" (ngSubmit)="onSubmit(participantForm)" class="register-form">
    <h2>Register Participant</h2>

    <div class="form-group">
      <label for="name">Name *</label>
      <input id="name" name="name" type="text" [(ngModel)]="participant.name" required />
      <div *ngIf="participantForm.submitted && !participant.name" class="error">Name is required</div>
    </div>

    <div class="form-group">
      <label for="email">Email *</label>
      <input id="email" name="email" type="email" [(ngModel)]="participant.email" required />
      <div *ngIf="participantForm.submitted && !participant.email" class="error">Valid email is required</div>
    </div>

    <div class="form-group">
      <label for="phone">Phone *</label>
      <input id="phone" name="phone" type="text" pattern="^[0-9]{10}$" [(ngModel)]="participant.phone" required />
      <div *ngIf="participantForm.submitted && !participant.phone" class="error">Phone number is required (10 digits)</div>
    </div>

    <div class="form-group">
      <label for="username">Username *</label>
      <input id="username" name="username" type="text" [(ngModel)]="participant.user.username" required minlength="4" />
      <div *ngIf="participantForm.submitted && !participant.user.username" class="error">Username is required</div>
    </div>

    <div class="form-group">
      <label for="password">Password *</label>
      <input id="password" name="password" type="password" [(ngModel)]="participant.user.password" required minlength="6" />
      <div *ngIf="participantForm.submitted && !participant.user.password" class="error">Password is required (min 6 characters)</div>
    </div>

    <button type="submit" [disabled]="participantForm.invalid || loading" class="register-btn">
      {{ loading ? 'Registering...' : 'Register Participant' }}
    </button>
  </form>

  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
</div>
