import { Component, OnInit } from '@angular/core';
import { EventService, Event } from '../services copy/event.service';  // Import your service and model

@Component({
  selector: 'app-modify-event',
  templateUrl: './modify-event.component.html',
  styleUrls: ['./modify-event.component.css']
})
export class ModifyEventComponent implements OnInit {
  events: Event[] = [];
  loading = true;
  errorMessage = '';
  selectedEvent: Event | null = null;

  constructor(private eventService: EventService) {}

  ngOnInit(): void {
    // Load all events initially (optional)
    this.loadEvents();
  }

  // Fetch the event by ID
  onSelectEvent(eventId: number): void {
    this.eventService.getEventById(eventId).subscribe({
      next: (data) => {
        this.selectedEvent = data;
        console.log('Event data fetched:', data);
      },
      error: (err) => {
        this.errorMessage = 'Error fetching event details.';
        console.error('Error fetching event:', err);
      }
    });
  }

  // Load all events (optional)
  loadEvents(): void {
    this.eventService.getEvents().subscribe({
      next: (data) => {
        this.events = data;
        this.loading = false;
        console.log('All events fetched:', data);
      },
      error: (err) => {
        this.errorMessage = 'Error fetching events.';
        console.error('Error fetching events:', err);
        this.loading = false;
      }
    });
  }

  // Modify the selected event
  modifyEvent(): void {
    if (this.selectedEvent) {
      this.eventService.updateEvent(this.selectedEvent.eventId!, this.selectedEvent).subscribe({
        next: (data) => {
          console.log('Event updated successfully:', data);
        },
        error: (err) => {
          this.errorMessage = 'Error updating event.';
          console.error('Error updating event:', err);
        }
      });
    }
  }
}
<div *ngIf="loading">Loading...</div>
<div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>

<!-- Event selection dropdown -->
<select (change)="onSelectEvent($event.target.value)">
  <option *ngFor="let event of events" [value]="event.eventId">{{ event.courseTitle }}</option>
</select>

<!-- Display selected event for modification -->
<div *ngIf="selectedEvent">
  <h3>Modify Event</h3>
  <form (ngSubmit)="modifyEvent()">
    <label for="courseTitle">Course Title</label>
    <input type="text" id="courseTitle" [(ngModel)]="selectedEvent.courseTitle" name="courseTitle" required />

    <label for="topicId">Topic ID</label>
    <input type="number" id="topicId" [(ngModel)]="selectedEvent.topicId" name="topicId" required />

    <label for="locationId">Location ID</label>
    <input type="number" id="locationId" [(ngModel)]="selectedEvent.locationId" name="locationId" required />

    <label for="speakerId">Speaker ID</label>
    <input type="number" id="speakerId" [(ngModel)]="selectedEvent.speakerId" name="speakerId" required />

    <label for="classSize">Class Size</label>
    <input type="number" id="classSize" [(ngModel)]="selectedEvent.classSize" name="classSize" required />

    <label for="numberOfDays">Number of Days</label>
    <input type="number" id="numberOfDays" [(ngModel)]="selectedEvent.numberOfDays" name="numberOfDays" required />

    <label for="startDate">Start Date</label>
    <input type="date" id="startDate" [(ngModel)]="selectedEvent.startDate" name="startDate" required />

    <label for="endDate">End Date</label>
    <input type="date" id="endDate" [(ngModel)]="selectedEvent.endDate" name="endDate" required />

    <button type="submit">Modify Event</button>
  </form>
</div>
