import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-participant-register',
  templateUrl: './participant-register.component.html',
  styleUrls: ['./participant-register.component.css']
})
export class ParticipantRegisterComponent {
  participant: any = {
    name: '',
    email: '',
    phone: '',
    user: {
      username: '',
      password: ''
    }
  };

  loading = false;
  successMessage = '';
  errorMessage = '';

  constructor(private http: HttpClient) {}

  // Form submission
  onSubmit(form: NgForm) {
    if (form.invalid) return; // Exit if form is invalid

    this.loading = true;  // Show loading spinner
    this.successMessage = '';  // Clear success message
    this.errorMessage = '';  // Clear error message

    // Construct payload based on form data
    const payload = {
      participantId: 0,  // Set to 0, backend will handle ID generation
      name: this.participant.name,
      email: this.participant.email,
      phone: this.participant.phone,
      user: {
        username: this.participant.user.username,  // Ensure user.username is populated
        password: this.participant.user.password  // Ensure user.password is populated
      }
    };

    console.log('Sending payload:', payload);

    // Send POST request to backend API for registration
    this.http.post('http://localhost:5070/api/Participants/Register', payload)
      .subscribe({
        next: (response) => {
          this.loading = false;  // Hide loading spinner
          this.successMessage = 'Participant registered successfully!';  // Show success message
          form.resetForm();  // Reset the form after successful registration
        },
        error: (err) => {
          this.loading = false;  // Hide loading spinner
          console.error('Error registering participant:', err);  // Log error
          this.errorMessage = 'Failed to register participant. Please check details.';  // Show error message
        }
      });
  }
}
<div class="page-container">
  <h2>Register Participant</h2>

  <form #registerForm="ngForm" (ngSubmit)="onSubmit(registerForm)">

    <!-- Participant Name -->
    <div class="form-group">
      <label for="name">Name:</label>
      <input [(ngModel)]="participant.name" name="name" id="name" type="text" required />
    </div>

    <!-- Participant Email -->
    <div class="form-group">
      <label for="email">Email:</label>
      <input [(ngModel)]="participant.email" name="email" id="email" type="email" required />
    </div>

    <!-- Participant Phone -->
    <div class="form-group">
      <label for="phone">Phone:</label>
      <input [(ngModel)]="participant.phone" name="phone" id="phone" type="text" required />
    </div>

    <!-- User Username -->
    <div class="form-group">
      <label for="username">Username:</label>
      <input [(ngModel)]="participant.user.username" name="username" id="username" type="text" required />
    </div>

    <!-- User Password -->
    <div class="form-group">
      <label for="password">Password:</label>
      <input [(ngModel)]="participant.user.password" name="password" id="password" type="password" required />
    </div>

    <!-- Submit Button -->
    <button type="submit" [disabled]="loading">Register</button>

    <!-- Loading Spinner -->
    <div *ngIf="loading">Registering...</div>

    <!-- Success/Error Messages -->
    <p *ngIf="successMessage" class="success">{{ successMessage }}</p>
    <p *ngIf="errorMessage" class="error">{{ errorMessage }}</p>
  </form>
</div>
