import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Participant {
  participantId: number;
  name: string;
}

interface Event {
  eventId: number;
  title: string;
}

@Component({
  selector: 'app-enroll',
  templateUrl: './participant-eventenroll.component.html',
  styleUrls: ['./participant-eventenroll.component.css']
})
export class EnrollComponent implements OnInit {
  participants: Participant[] = [];
  events: Event[] = [];

  participantId: number | null = null;
  eventId: number | null = null;

  message = '';
  loading = false;

  private participantApi = 'http://localhost:5070/api/Participants';
  private eventApi = 'http://localhost:5070/api/Events';
  private enrollApi = 'http://localhost:5070/api/Enrollments';

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.fetchParticipants();
    this.fetchEvents();
  }

  fetchParticipants() {
    this.http.get<Participant[]>(this.participantApi).subscribe({
      next: (data) => (this.participants = data),
      error: (err) => console.error('Error fetching participants', err)
    });
  }

  fetchEvents() {
    this.http.get<Event[]>(this.eventApi).subscribe({
      next: (data) => (this.events = data),
      error: (err) => console.error('Error fetching events', err)
    });
  }

  enroll() {
    if (!this.participantId || !this.eventId) {
      this.message = 'Please select both participant and event.';
      return;
    }

    this.loading = true;
    this.message = '';

    const body = {
      participantId: this.participantId,
      eventId: this.eventId
    };

    this.http.post<any>(this.enrollApi, body).subscribe({
      next: (res) => {
        this.message = res.message || 'Enrollment successful!';
        this.loading = false;
      },
      error: (err) => {
        console.error('Error enrolling', err);
        this.message = err.error || 'Enrollment failed!';
        this.loading = false;
      }
    });
  }
}
<div class="enroll-container">
  <h2>Enroll Participant in Event</h2>

  <!-- Participant Selection -->
  <div class="form-group">
    <label for="participant">Select Participant:</label>
    <select
      id="participant"
      [(ngModel)]="participantId"
      class="form-control"
    >
      <option [ngValue]="null">-- Select Participant --</option>
      <option *ngFor="let p of participants" [value]="p.participantId">
        {{ p.name }}
      </option>
    </select>
  </div>

  <!-- Event Selection -->
  <div class="form-group">
    <label for="event">Select Event:</label>
    <select
      id="event"
      [(ngModel)]="eventId"
      class="form-control"
    >
      <option [ngValue]="null">-- Select Event --</option>
      <option *ngFor="let e of events" [value]="e.eventId">
        {{ e.title }}
      </option>
    </select>
  </div>

  <!-- Enroll Button -->
  <div class="form-group">
    <button
      (click)="enroll()"
      class="btn btn-primary"
      [disabled]="loading"
    >
      {{ loading ? 'Enrolling...' : 'Enroll' }}
    </button>
  </div>

  <!-- Message Display -->
  <div *ngIf="message" class="alert" [ngClass]="{'alert-success': message.includes('success'),
    'alert-danger': message.includes('failed') || message.includes('Error') || message.includes('Please')
  }">
    {{ message }}
  </div>
</div>
