import { Component, OnInit } from '@angular/core';
import { SpeakerService, EventModel } from '../speaker.service';

@Component({
  selector: 'app-view-events',
  templateUrl: './view-events.component.html',
  styleUrls: ['./view-events.component.css']
})
export class ViewEventsComponent implements OnInit {
  events: EventModel[] = [];
  loading = false;
  errorMessage = '';

  // Example: This should come from logged-in user context/JWT
  speakerId: number = 1;  

  constructor(private speakerService: SpeakerService) {}

  ngOnInit(): void {
    this.fetchEvents();
  }

  fetchEvents() {
    this.loading = true;
    this.errorMessage = '';

    this.speakerService.getEvents(this.speakerId).subscribe({
      next: (data) => {
        this.events = data;
        this.loading = false;
      },
      error: (err) => {
        this.errorMessage = err.error?.message || '‚ùå Failed to load events';
        this.loading = false;
      }
    });
  }
}
