import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface User {
  username: string;
  password: string;
}

interface Participant {
  name: string;
  email: string;
  phone: string;
  user: User;
}

@Component({
  selector: 'app-participant-register',
  templateUrl: './participant-register.component.html',
  styleUrls: ['./participant-register.component.css']
})
export class ParticipantRegisterComponent {
  participant: Participant = {
    name: '',
    email: '',
    phone: '',
    user: {
      username: '',
      password: ''
    }

  };

  loading = false;
  successMessage = '';
  errorMessage = '';

  private apiUrl = 'http://localhost:5070/api/Participants/Register';

  constructor(private http: HttpClient) {}

  onSubmit(form: any) {
    if (form.invalid) {
      this.errorMessage = 'Please fill in all required fields correctly.';
      return;
    }

    this.loading = true;
    this.successMessage = '';
    this.errorMessage = '';

    // Map lowercase `user` to uppercase `User` for backend
    const payload = {
      name: this.participant.name,
      email: this.participant.email,
      phone: this.participant.phone,
      User: this.participant.user // <-- backend expects "User"
    };

    console.log('Sending participant:', payload);

    this.http.post<any>(this.apiUrl, payload).subscribe({
      next: (res) => {
        this.successMessage = res.message || 'Participant registered successfully!';
        this.errorMessage = '';
        this.loading = false;

        form.resetForm();
        this.participant = {
          name: '',
          email: '',
          phone: '',
          user: {
            username: '',
            password: ''
          }
        };
      },
      error: (err) => {
        this.errorMessage = err.error || 'An error occurred during registration.';
        this.successMessage = '';
        this.loading = false;
      }
    });
  }
}
<div class="register-container">
  <form
    class="register-form"
    #participantForm="ngForm"
    (ngSubmit)="onSubmit(participantForm)"
  >
    <h2>Register Participant</h2>

    <!-- Name -->
    <div class="form-group">
      <label>Name *</label>
      <input type="text" [(ngModel)]="participant.name" name="name" required />
      <div *ngIf="participantForm.submitted && !participant.name" class="error">
        Name is required
      </div>
    </div>

    <!-- Email -->
    <div class="form-group">
      <label>Email *</label>
      <input
        type="email"
        [(ngModel)]="participant.email"
        name="email"
        required
        email
      />
      <div *ngIf="participantForm.submitted && !participant.email" class="error">
        Valid email is required
      </div>
    </div>

    <!-- Phone -->
    <div class="form-group">
      <label>Phone *</label>
      <input
        type="text"
        [(ngModel)]="participant.phone"
        name="phone"
        required
        pattern="^[0-9]{10}$"
      />
      <div *ngIf="participantForm.submitted && !participant.phone" class="error">
        Phone number is required (10 digits)
      </div>
    </div>

    <!-- Username -->
    <div class="form-group">
      <label>Username *</label>
      <input
        type="text"
        [(ngModel)]="participant.user.username"
        name="username"
        required
        minlength="4"
        [ngModelOptions]="{ standalone: true }"
      />
      <div
        *ngIf="participantForm.submitted && !participant.user?.username"
        class="error"
      >
        Username is required
      </div>
    </div>

    <!-- Password -->
    <div class="form-group">
      <label>Password *</label>
      <input
        type="password"
        [(ngModel)]="participant.user.password"
        name="password"
        required
        minlength="6"
        [ngModelOptions]="{ standalone: true }"
      />
      <div
        *ngIf="participantForm.submitted && !participant.user?.password"
        class="error"
      >
        Password is required (min 6 characters)
      </div>
    </div>

    <!-- Submit Button -->
    <button
      type="submit"
      class="register-btn"
      [disabled]="participantForm.invalid || loading"
    >
      {{ loading ? 'Registering...' : 'Register Participant' }}
    </button>
  </form>

  <!-- Messages -->
  <div *ngIf="successMessage" class="success">{{ successMessage }}</div>
  <div *ngIf="errorMessage" class="error">{{ errorMessage }}</div>
</div>
