import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { EventService, Event } from '../services copy/event.service';

@Component({
  selector: 'app-modify-event',
  templateUrl: './modify-event.component.html',
  styleUrls: ['./modify-event.component.css']
})
export class ModifyEventComponent implements OnInit {

  events: Event[] = [];
  selectedEventId: number | null = null;

  event: Event = {
    eventId: 0,
    courseTitle: '',
    topicId: 0,
    locationId: 0,
    speakerId: 0,
    classSize: 0,
    numberOfDays: 0,
    startDate: '',
    endDate: ''
  };

  topics: any[] = [];
  locations: any[] = [];
  speakers: any[] = [];

  constructor(private eventService: EventService, private router: Router) {}

  ngOnInit(): void {
    this.loadDropdowns();
    this.loadEvents();
  }

  // Load dropdown data (Topics, Locations, Speakers)
  loadDropdowns() {
    this.eventService.getTopics().subscribe((data) => (this.topics = data));
    this.eventService.getLocations().subscribe((data) => (this.locations = data));
    this.eventService.getSpeakers().subscribe((data) => (this.speakers = data));
  }

  // Load the events to show in the select dropdown
  loadEvents() {
    this.eventService.getEvents().subscribe((data) => (this.events = data));
  }

  // Select an event to modify
  onSelectEvent() {
    if (this.selectedEventId !== null) {
      // Make sure the selected eventId exists before making the API call
      this.eventService.getEventById(this.selectedEventId).subscribe({
        next: (data) => {
          this.event = data; // Populate the form with the selected event
        },
        error: (err) => {
          console.error('Error fetching event:', err);
          alert('Event not found or error fetching event.');
        }
      });
    }
  }

  // Update the selected event
  updateEvent() {
    if (this.selectedEventId !== null) {
      this.eventService.updateEvent(this.selectedEventId, this.event).subscribe(
        () => {
          alert('Event updated successfully!');
          this.router.navigate(['/view-event']);
        },
        (err) => {
          console.error('Error updating event:', err);
          alert('Failed to update event.');
        }
      );
    }
  }
}
<div class="form-container">
  <h2>Modify Event</h2>

  <!-- Step 1: Select Event -->
  <label>Select Event to Modify</label>
  <select [(ngModel)]="selectedEventId" name="selectedEventId" (change)="onSelectEvent()" required>
    <option value="" disabled selected>Select an Event</option>
    <option *ngFor="let e of events" [value]="e.eventId">{{ e.courseTitle }}</option>
  </select>

  <!-- Show error message if any -->
  <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>

  <!-- Step 2: Form appears only after selecting event -->
  <div *ngIf="selectedEventId !== null">
    <form (ngSubmit)="updateEvent()">

      <label>Course Title</label>
      <input type="text" [(ngModel)]="event.courseTitle" name="courseTitle" required />

      <label>Topic</label>
      <select [(ngModel)]="event.topicId" name="topicId" required>
        <option value="" disabled>Select a Topic</option>
        <option *ngFor="let topic of topics" [value]="topic.topicId">{{ topic.topicName }}</option>
      </select>

      <label>Location</label>
      <select [(ngModel)]="event.locationId" name="locationId" required>
        <option value="" disabled>Select a Location</option>
        <option *ngFor="let location of locations" [value]="location.locationId">
          {{ location.city }} - {{ location.state }}
        </option>
      </select>

      <label>Speaker</label>
      <select [(ngModel)]="event.speakerId" name="speakerId" required>
        <option value="" disabled>Select a Speaker</option>
        <option *ngFor="let speaker of speakers" [value]="speaker.speakerId">{{ speaker.name }}</option>
      </select>

      <label>Class Size</label>
      <input type="number" [(ngModel)]="event.classSize" name="classSize" required />

      <label>Number of Days</label>
      <input type="number" [(ngModel)]="event.numberOfDays" name="numberOfDays" required />

      <label>Start Date</label>
      <input type="date" [(ngModel)]="event.startDate" name="startDate" required />

      <label>End Date</label>
      <input type="date" [(ngModel)]="event.endDate" name="endDate" required />

      <button type="submit">Update Event</button>
    </form>
  </div>
</div>
