import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-event-enroll',
  templateUrl: './participant-eventenroll.component.html',
  styleUrls: ['./participant-eventenroll.component.css']
})
export class EventEnrollComponent {
  participantId: number | null = null;
  eventId: number | null = null;
  loading = false;
  message = '';

  constructor(private http: HttpClient) {}

  enroll() {
    if (!this.participantId || !this.eventId) {
      this.message = 'Please select both participant and event.';
      return;
    }

    this.loading = true;

    this.http
      .post(
        `http://localhost:5070/api/Participants/${this.participantId}/Enroll/${this.eventId}`,
        {}
      )
      .subscribe({
        next: (res: any) => {
          this.message = res.message || 'Enrollment successful!';
          this.loading = false;
        },
        error: (err) => {
          console.error('Error enrolling:', err);
          this.message = 'Enrollment failed. Please try again.';
          this.loading = false;
        }
      });
  }
}
<h2>Enroll in Event</h2>
<form (ngSubmit)="enroll()">
  <input [(ngModel)]="participantId" name="participantId" placeholder="Participant ID" required />
  <input [(ngModel)]="eventId" name="eventId" placeholder="Event ID" required />
  <button type="submit">Enroll</button>
</form>

<p>{{ message }}</p>
