import { Component, OnInit } from '@angular/core';
import { ParticipantsService, Feedback, Event, Speaker } from '../participant.service';

@Component({
  selector: 'app-feedback',
  templateUrl: './participant-feedback.component.html',
  styleUrls: ['./participant-feedback.component.css']
})
export class ParticipantFeedbackComponent implements OnInit {
  // Define feedback object according to the Feedback interface
  feedback: Feedback = { eventID: 0, speakerID: 0, feedback_remarks: '' };
  events: Event[] = []; // Array to hold event list
  speakers: Speaker[] = []; // Array to hold speaker list
  message: string = ''; // To show the success or error message
  isSuccess: boolean = false; // Flag to track success or failure

  constructor(private service: ParticipantsService) {}

  // ngOnInit lifecycle hook to load events and speakers from the service
  ngOnInit() {
    // Fetch events and speakers when the component is initialized
    this.loadEvents();
    this.loadSpeakers();
  }

  // Method to fetch events from the API
  loadEvents() {
    this.service.getEvents().subscribe(events => {
      this.events = events;
    });
  }

  // Method to fetch speakers from the API
  loadSpeakers() {
    this.service.getSpeakers().subscribe(speakers => {
      this.speakers = speakers;
    });
  }

  // Method to submit the feedback
  submit() {
    this.service.submitFeedback(this.feedback).subscribe({
      next: (res) => {
        this.message = res.message;
        this.isSuccess = true;
      },
      error: (err) => {
        this.message = err.error || 'An error occurred while submitting the feedback.';
        this.isSuccess = false;
      }
    });
  }
}
<div class="page-container">
  <h2>Submit Feedback</h2>
  <form (ngSubmit)="submit()">
    <div class="form-group">
      <label for="eventId">Event ID:</label>
      <input [(ngModel)]="feedback.eventID" name="eventId" id="eventId" type="number" placeholder="Enter Event ID" required />
    </div>

    <div class="form-group">
      <label for="speakerId">Speaker ID:</label>
      <input [(ngModel)]="feedback.speakerID" name="speakerId" id="speakerId" type="number" placeholder="Enter Speaker ID" required />
    </div>

    <div class="form-group">
      <label for="remarks">Feedback Remarks:</label>
      <textarea [(ngModel)]="feedback.feedback_remarks" name="remarks" id="remarks" rows="4" placeholder="Enter your feedback here..." required></textarea>
    </div>

    <button type="submit">Submit Feedback</button>
  </form>

  <p *ngIf="message" [ngClass]="{ 'success': isSuccess, 'error': !isSuccess }">{{ message }}</p> <!-- Success/Error Message -->
</div>
