import { Component } from '@angular/core';
import { ParticipantsService } from '../participant.service';

@Component({
  selector: 'app-event-enroll',
  templateUrl: './participant-eventenroll.component.html',
  styleUrls: ['./participant-eventenroll.component.css']
})
export class ParticipantEventenrollComponent {
  participantId!: number;
  eventId!: number;
  message = '';

  constructor(private service: ParticipantsService) {}

  enroll() {
    this.service.enroll(this.participantId, this.eventId).subscribe({
      next: res => this.message = res.message,
      error: err => this.message = err.error
    });
  }
}
<h2>Enroll in Event</h2>
<form (ngSubmit)="enroll()">
  <input [(ngModel)]="participantId" name="participantId" placeholder="Participant ID" required />
  <input [(ngModel)]="eventId" name="eventId" placeholder="Event ID" required />
  <button type="submit">Enroll</button>
</form>

<p>{{ message }}</p>
