import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-speaker-sidebar',
  templateUrl: './speaker-sidebar.component.html',
  styleUrls: ['./speaker-sidebar.component.css']
})
export class SpeakerSidebarComponent {
  constructor(private authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout();         // remove token
    this.router.navigate(['/signin']); // redirect to signin
  }
}
<div class="speaker-layout">
  <!-- Sidebar -->
  <aside class="sidebar">
    <h2 class="logo"> Speaker</h2>
    <nav>
      <ul>
        <li><a routerLink="register" routerLinkActive="active"> Register Speaker</a></li>
        <li><a routerLink="events" routerLinkActive="active"> View Events</a></li>
        <li><a routerLink="enroll"> Enroll Event</a></li>
        <li><a routerLink="feedback" routerLinkActive="active"> View Feedback</a></li>
      </ul>
    </nav>
  </aside>


  <!-- Main content -->
  <main class="content">
    <router-outlet></router-outlet>
  </main>
</div>
